package com.baidu.tcpdump;import android.content.BroadcastReceiver;import android.content.Context;import android.content.Intent;import android.util.Log;public class BootReceiver extends BroadcastReceiver {     public void onReceive(Context context, Intent intent) {             	if(intent.getAction().equals(Intent.ACTION_BOOT_COMPLETED)){        	              System.out.println("----------******----------");             System.out.println("Boot this system!!!");             Log.i("MainActivity", "Boot this system!!!");         }     } }