package com.baidu.tcpdump;import android.content.BroadcastReceiver;import android.content.Context;import android.content.Intent;import android.util.Log;public class ShutdownReceiver extends BroadcastReceiver {     public void onReceive(Context context, Intent intent) {                 if(intent.getAction().equals(Intent.ACTION_SHUTDOWN)){        	              System.out.println("----------******----------");             System.out.println("Shut down this system!!!");             Log.i("MainActivity", "Shut down this system!!!");                          Intent activity = new Intent();             activity.setClass(context, MainActivity.class);             activity.putExtra("cmd", "stop_tcpdump");                          context.startActivity(activity);         }     } }